import React, { Component } from 'react';
import {Card, CardBody, CardImg,Row, Col,  Breadcrumb, CardHeader, Container} from 'reactstrap';
import Header from './Header';
import Breadcrumbs from './Breadcrumbs';
import Footer from './Footer'
import Shipping from "../assets/images/ic_shipping.png";
import CurrencyFormat from 'react-currency-format';
import { connect } from 'react-redux';

class ProductList extends Component{
	constructor(props) {
		super(props);
		this.state = {}
		this.cardProduct = this.cardProduct.bind(this);
	}

	productCall(id){
		this.props.history.push({pathname: '/items/'+id});
	}

	cardProduct(obj){
		var product = [];
		const productCall = this.productCall.bind(this);
		if(obj.query.items != undefined && obj.query.items.length > 0){
			var result = obj.query.items;
			const pos = {img:{size:2, offset:1} , state:{size:2}, desc:{size:6}}
			var price;
			var freeShipping;
			result.forEach(val => {
				price = Math.round(val.price.amount, -1);
				freeShipping = (val.free_shipping)?(<div className="shipping"><CardImg width='100%' src={Shipping}/></div>):"";
				product.push(
					<div>
						<Row>
							<Col xs={pos.img} sm={pos.img} md={pos.img} className="prodLCol1">
								<Container fluid={true}>
									<div className="thuname">
										<img src={val.picture} className="mx-auto d-block" onClick={() => productCall(val.id)}/>
									</div>
								</Container>
							</Col>
							<Col xs={pos.desc} sm={pos.desc} md={pos.desc} className="prodLCol2">
								<Container fluid={true}>
									<div className="price" onClick={() => productCall(val.id)}>
										<CurrencyFormat value={price} displayType={'text'} thousandSeparator={true} prefix={'$ '} renderText={value => value} /> 
										{freeShipping}
									</div>
									<div className="info">
										<div className="title">{val.title}</div>
										<div className="condition">{val.condition}</div>
									</div>
								</Container>
							</Col>
							<Col xs={pos.desc} sm={pos.state} md={pos.state} className="prodLCol3">
								<Container fluid={true}>
									<div className="state">{val.state}</div>
								</Container>
							</Col>
						</Row>
					</div>
				);
			});
		}else{
			product.push(
				<Row key="0" className="prodNull">
					<Col className="h-100 d-table no-gutter"></Col>
				</Row>
			);
		}
		return product;
	}


	render(){
		const pos = {center:{size: 10, offset: 1}}
		return(
                <Container fluid={true} style={{padding:"0px"}}>
					<Header {...this.props} pos={pos}/>
                    <Breadcrumbs pos={pos}/>
					<CardBody>
						{this.cardProduct(this.props.obj)}
					</CardBody>
                    <Footer pos={pos}/>
                </Container>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch,
    }
}

export default connect(mapStateToProps)(ProductList);
