import React, { Suspense, Component } from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { createStore, combineReducers, applyMiddleware } from 'redux';
import promise from "redux-promise-middleware";
import thunk from 'redux-thunk'
import ReducerSearch from './components/RSearch';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import  ProductList from "./canvan/ProductList";
import  Product from "./canvan/Product"
import "./styles/Canvan.scss";

//Utilizamos el combineReducers con el fin de administrar los estados, de esta manera cada estado tiene asociado  
//una funcion que se encargara de su adminsitración 
const reducer = combineReducers({
    ReducerSearch: ReducerSearch.RSearch, 
});

//Donde almacenamos nuestro estado
const store = createStore(reducer, applyMiddleware(thunk, promise));

class RouterDeLienzo extends Component{

	constructor(props) {
		super(props);
		this.state = {}
	}

	render(){
		return(
            <BrowserRouter ref={this.props.innerRef}>
                <Provider store={store} >
					<Suspense fallback={<div>Loading Módulo...</div>}>
						<Switch>
							<Route path="/" component={ProductList} exact/>
							<Route path="/items"  component={ProductList} exact/>
							<Route path="/items/:id" component={Product} exact/>
						</Switch>
					</Suspense>
                </Provider>
            </BrowserRouter>
		)	
	}
}

const BlockWithRef = React.forwardRef((props, ref) => {
   return <RouterDeLienzo innerRef={ref} {...props} />
})

ReactDOM.render(
	<BlockWithRef />,
    document.getElementById('root')
);
