import React, { Component } from 'react';
import {Card, CardBody, CardImg, Row, Col, Container} from 'reactstrap';
import { connect } from 'react-redux';
import DAO from "../components/Dao";
import Header from './Header';
import Breadcrumbs from './Breadcrumbs';
import Footer from './Footer'

class Product extends Component{
	constructor(props) {
		super(props);
        this.state = {}
        this.cardProduct = this.cardProduct.bind();
    }

    componentDidMount(){this.props.callDispatch(this.props.match.params.id);}

    cardProduct(obj){
        var item = obj.item;
        if(item != undefined){
            const pos = {img:{size:7, offset:1} , price:{size:3}, desc:{size:10, offset:1}}
            return (
                <div>
                    <Row>
                        <Col xs={pos.img} sm={pos.img} md={pos.img} style={{backgroundColor:"withe"}}>
                            <Container fluid={true} style={{backgroundColor:"brown", minHeight:"680px"}}>
                                dfsdf
                            </Container>
                        </Col>
                        <Col xs={pos.price} sm={pos.price} md={pos.price} style={{backgroundColor:"withe"}} >
                            <Container fluid={true} style={{backgroundColor:"coral", minHeight:"680px"}}>
                                dfsdf
                            </Container>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={pos.desc} sm={pos.desc} md={pos.desc} style={{backgroundColor:"withe"}}>
                            <Container fluid={true} style={{backgroundColor:"brown"}}>
                                dfsdf
                            </Container>
                        </Col>
                    </Row>
                </div>
            )
        }
    }
//className="offset-md-1"
	render(){
        const pos = {pag:"12", center:{size: 10, offset: 1}, img:{size:7, offset:1} , price:{size:3}}
		return(
            <Container fluid={true} className="p-0">
				<Header {...this.props} pos={pos}/>
                <Breadcrumbs pos={pos}/>
                <CardBody>
                    {this.cardProduct(this.props.obj)}
                </CardBody>
                <Footer pos={pos}/>
            </Container>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch.item,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(data) => {
            DAO.getProduct(dispatch, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Product);
