import React, { Component } from 'react';
import {Dropdown, DropdownItem, DropdownMenu, DropdownToggle, Nav, Button, Input, InputGroup, InputGroupAddon, InputGroupText, Col, Row} from 'reactstrap';

class Header extends Component{

    constructor(props) {
		super(props);
		this.toggle = this.toggle.bind(this);
		this.state = {
		  dropdownOpen: false
		};
	}

	toggle() {
		this.setState({
		  dropdownOpen: !this.state.dropdownOpen,
		});
	}

	logout(){
		firebase.auth().signOut().then(function() {
            window.location = "#/login";
        }).catch(function(error) {
            console.log("Se ha precentado un erro al salir de la aplicaci√≥n "+error)
        });
	}

	render(){
		return(
			<Row className="d-flex justify-content-center">
				<Col xs="1" sm="1" md="1"></Col>
				<Col xs="5" sm="5" md="5">
                    Logo
				</Col>
				<Col xs="5" sm="5" md="5" className="text-right">
					<Row>
						<Col xs="9" sm="9" md="9">
							<Row>
								<Col xs={{ size: 10, offset: 2 }} sm={{ size: 10, offset: 2 }} md={{ size: 10, offset: 2 }}>
									<InputGroup  seamless>
										<InputGroupAddon addonType="prepend">
											<InputGroupText style={{ background:'white'}}>
												<FontAwesomeIcon icon={faSearch} />
											</InputGroupText>
										</InputGroupAddon>
										<Input type="text" id="order" name="order" placeholder="¬øQue quiere buscar?" style={{ borderLeftColor:'#fff'}}/>
										<InputGroupAddon addonType="append">
											<Button type="button" style={{ background:'#14bec9', borderColor:'#ced4da'}}>Buscar</Button>
										</InputGroupAddon>
									</InputGroup>
								</Col>
							</Row>
						</Col>
						<Col xs="3" sm="3" md="3">
							<Nav className="ml-auto" navbar>
								<Row>
									<Col xs="5" sm="5" md="5">
										<Dropdown nav isOpen={this.state.dropdownOpen} toggle={this.toggle}>
											<DropdownToggle nav>
												<FontAwesomeIcon icon={faUser} style={{color:'darkgray'}} />
											</DropdownToggle>
											<DropdownMenu  className="dropdown-menu-lg">
												<DropdownItem header tag="div" className="text-center"><strong>Mi Cuenta</strong></DropdownItem>
												<DropdownItem><FontAwesomeIcon icon={faUser} size="xs" style={{color:'darkgray', margin:'2px'}}/>&nbsp;&nbsp;Mi Perfil</DropdownItem>
												<DropdownItem onClick={this.logout.bind(this)}><FontAwesomeIcon icon={faUser} size="xs" style={{color:'darkgray', margin:'2px'}}/>&nbsp;&nbsp;Cerrar</DropdownItem>
											</DropdownMenu>
										</Dropdown>
									</Col>
									<Col xs="5" sm="5" md="5">
										<Row>
											<Dropdown nav>
												<DropdownToggle nav>
													<FontAwesomeIcon icon={faShoppingCart} style={{color:'darkgray'}}/>
												</DropdownToggle>
											</Dropdown>
											<Dropdown nav>
											    <div style={{backgroundColor:"red", borderRadius:"100px", color:"white", textAlign:"center", fontSize:"small"}}>&nbsp;11&nbsp;</div>
											</Dropdown>
										</Row>
									</Col>
								</Row>
							</Nav>
						</Col>
					</Row>
				</Col>
				<Col xs="1" sm="1" md="1"></Col>
			</Row>
		)
	}
}

export default Header;
