import React, { Component } from 'react';
import {CardBody, Button, Row, Col, Container} from 'reactstrap';
import { connect } from 'react-redux';
import DAO from "../components/Dao";
import Header from './Header';
import Breadcrumbs from './Breadcrumbs';
import Footer from './Footer'
import parse from 'html-react-parser';
import CurrencyFormat from 'react-currency-format';

class Product extends Component{
	constructor(props) {
		super(props);
        this.cardProduct = this.cardProduct.bind();
    }

    componentDidMount(){this.props.callDispatch(this.props.match.params.id);}

    cardProduct(obj){
        var item = obj.item;
        if(item){
            var price = Math.round(item.price.amount, -1);
            const pos = {img:{size:7, offset:1} , price:{size:3}, desc:{size:10, offset:1}}
            return (
                <div>
                    <Row>
                        <Col md={pos.img} className="prodCol1">
                            <Container fluid={true}>
                                <img src={item.picture} className="mx-auto d-block" alt={item.titles}/>
                            </Container>
                        </Col>
                        <Col md={pos.price} className="prodCol2" >
                            <Container fluid={true}>
                                <Row>
                                    <Col className="condition">
                                        {item.condition} - {item.sold_quantity} Vendidos
                                    </Col>
                                </Row>
                                <Row>
                                    <Col className="title">
                                        {item.title}
                                    </Col>
                                </Row>
                                <Row>
                                    <Col className="price">
                                        <CurrencyFormat value={price} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
                                    </Col>
                                </Row>
                                <Row>
                                    <Col className="button">
                                        <Button block color="primary">Comprar</Button>
                                    </Col>
                                </Row>
                            </Container>
                        </Col>
                    </Row>
                    <Row>
                        <Col md={pos.desc} className="prodCol3" >
                            <Container fluid={true}>
                                <Row>
                                    <Col className="title">
                                        Descripci√≥n del producto
                                    </Col>
                                </Row>
                                <Row>
                                    <Col className="content">
                                        {parse(item.description)}
                                    </Col>
                                </Row>
                            </Container>
                        </Col>
                    </Row>
                </div>
            )
        }
    }
//className="offset-md-1"
	render(){
		return(
            <Container fluid={true} className="p-0 bg-light">
				<Header {...this.props}/>
                <Breadcrumbs/>
                <CardBody>
                    {this.cardProduct(this.props.obj)}
                </CardBody>
                <Footer/>
            </Container>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch.item,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(data) => {
            DAO.getProduct(dispatch, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Product);
