import React, { Component } from 'react';
import {Card, CardBody, CardImg,Row, Col, Container} from 'reactstrap';
import Shipping from "../assets/images/ic_shipping.png";
import CurrencyFormat from 'react-currency-format';
import { connect } from 'react-redux';
import DAO from "../components/Dao";

class Product extends Component{
	constructor(props) {
		super(props);
		this.state = {}
    }

    componentDidMount(){
        this.props.callDispatch(this.props.match.params.id);
    }

	render(){
		return(
			<Card>
				<Row>
                    <Col>
                        Foto
                    </Col>
                    <Col>Descripcion</Col>
                </Row>
                <Row>
                    <Col>Titulo</Col>
                </Row>
			</Card>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(type, data) => {
            DAO.getProduct(dispatch, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Product);
