const objQuery = {query:[]}

//se pasa un estado por defecto para definicion de estructura
function RSearch(state = objQuery, action){

    var NewState = Object.assign({},state);
    if(action.type === "findProducts"){
        //console.log("llego la solicitud DbCrud dbMload")
        var queryUrl = "http://localhost:5567/api/items?q="+action.data;
        console.log(queryUrl);
        fetch(queryUrl)
        .then(data => data.json())
        .then(data => {
            NewState.query = data;
            return NewState;
            //dispatch({ type: type, data: data, function:listenerFuntion }) 
        });
        //.catch(function (error) {console.log("Error cargando datos " + error);});
        //console.log(action.data);
    }

} 

const ReducerSearch = {RSearch}

export default ReducerSearch;
