import React, { Component } from 'react';
import {Card, CardBody, CardImg,Row, Col, Container} from 'reactstrap';
import Shipping from "../assets/images/ic_shipping.png";
import CurrencyFormat from 'react-currency-format';
import { connect } from 'react-redux';

class ProductList extends Component{
	constructor(props) {
		super(props);
		this.state = {}
		this.cardProduct = this.cardProduct.bind(this);
	}

	productCall(id){
		console.log(id);
	}

	cardProduct(obj){
		var product = [];
		const productCall = this.productCall.bind(this);
		if(obj.query.items != undefined && obj.query.items.length > 0){
			var result = obj.query.items;
			const pos = {img:"2", content:"10", info:"9", from:"2"}
			var price;
			var freeShipping;
			result.forEach(val => {
				price = Math.round(val.price.amount, -1);
				freeShipping = (val.free_shipping)?(<div className="shipping"><CardImg width='100%' src={Shipping}/></div>):"";
				product.push(
					<Row key={val.id} className="separation">
						<Col xs={pos.img} sm={pos.img} md={pos.img} className="h-100 d-table no-gutter">
							<div className="d-table-cell align-middle prodImg cursor"  onClick={() => productCall(val.id)}>
								<CardImg src={val.picture}/>
							</div>
						</Col>
						<Col xs={pos.content} sm={pos.content} md={pos.content} className="no-gutter h-100 d-table">
							<Row className="info">
								<Col xs={pos.info} sm={pos.info} md={pos.info}>
									<div className="price">
										<div className="cursor" onClick={() => productCall(val.id)}>
											<CurrencyFormat value={price} displayType={'text'} thousandSeparator={true} prefix={'$'} renderText={value => value} />
										</div> {freeShipping}
									</div>
									<div>
										<div className="inf1">{val.title}</div>
										<div className="inf2">{val.condition}</div>
									</div>
								</Col>
								<Col xs={pos.from} sm={pos.from} md={pos.from} className="d-table-cell align-bottom from">{val.state}</Col>
							</Row>
						</Col>
					</Row>
				);
			});
		}else{
			product.push(
				<Row key="0" className="prodNull">
					<Col className="h-100 d-table no-gutter"></Col>
				</Row>
			);
		}
		return product;
	}

	render(){
		return(
			<Card>
				{this.cardProduct(this.props.obj)}
			</Card>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(type, self) => {
            dispatch({type: type, data: self });
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
