import React, { Component } from 'react';
import {Row, Col, CardImg, InputGroupAddon, InputGroup, InputGroupText, Input, CardHeader} from 'reactstrap';
import Logo from "../assets/images/Logo_ML.png";
import searchIcon from "../assets/images/ic_Search.png";
import { connect } from 'react-redux';
import DAO from "../components/Dao";
import queryString from 'query-string';

class Header extends Component{

    constructor(props) {
		super(props);
        this.state = {
            find:(this.props.match.params.search)?this.props.match.params.search:"",
        };
    }

    componentDidMount(){
        let find = queryString.parse(this.props.location.search);
        find = (find.search != undefined)?find.search:"";
        this.setState({find},() => {
            this.callFind();
        });
    }

    callFind(){
        var search = (this.state.find != "")? '?search='+this.state.find:"";
        this.props.history.push({
            pathname: '/items',
            search
        })
        this.props.callDispatch(this.state.find);
    }

	render(){
        const pos = {logo:"1", barSerch:"11"}
		return(
            <CardHeader>
                <Col xs={this.props.pos.center} sm={this.props.pos.center} md={this.props.pos.center}>         
                    <Row>
                        <Col xs={pos.logo} sm={pos.logo} md={pos.logo} className="logo" >
                            <a href='/'>Donde comprar y vender<CardImg src={Logo}/></a>
                        </Col>
                        <Col xs={pos.barSerch} sm={pos.barSerch} md={pos.barSerch} className="barSearch">
                            <InputGroup>
                                <Input type="text" id="name" placeholder="Nunca dejes de buscar" defaultValue={this.state.find} onChange={(find) => {this.setState({find:find.target.value})}} onKeyDown={(e) => {if (e.key === 'Enter') this.callFind()}}/>
                                <InputGroupAddon addonType="prepend" onClick={this.callFind.bind(this)}>
                                    <InputGroupText><CardImg width="100%" src={searchIcon}/></InputGroupText>
                                </InputGroupAddon>
                            </InputGroup>
                        </Col>
                    </Row>
                </Col>
            </CardHeader>
		)
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(data) => {
            DAO.getProducts(dispatch, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
