import React, { Component } from 'react';
import {Card, CardBody, CardImg, Row, Col} from 'reactstrap';
import { connect } from 'react-redux';
import DAO from "../components/Dao";
import Header from './Header';
import Breadcrumbs from './Breadcrumbs';
import Footer from './Footer'

class Product extends Component{
	constructor(props) {
		super(props);
        this.state = {}
        this.cardProduct = this.cardProduct.bind();
    }

    componentDidMount(){this.props.callDispatch(this.props.match.params.id);}

    cardProduct(obj){
        console.log(obj);
        var item = obj.item;
        return (
            <Card>
				<Row>
                    <Col>
                        <CardImg src={item.picture}/>
                    </Col>
                    <Col>Description Price</Col>
                </Row>
                <Row><Col>Title</Col></Row>
                <Row><Col>Description</Col></Row>
			</Card>
        )
    }

	render(){
        const pos = {pag:"12", center:{size: 10, offset: 1}}
		return(
            <Row>
                <Col xs={pos.pag} sm={pos.pag} md={pos.pag}>
					<Header {...this.props} pos={pos}/>
                    <Breadcrumbs pos={pos}/>
                    <CardBody>
                        <Col xs={pos.center} sm={pos.center} md={pos.center}>
							{this.cardProduct(this.props.obj)}
                        </Col>
                    </CardBody>
                    <Footer pos={pos}/>
                </Col>
            </Row>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch.item,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(data) => {
            DAO.getProduct(dispatch, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Product);
