const objQuery = {query:[], item:[]}

//se pasa un estado por defecto para definicion de estructura
function RSearch(state = objQuery, action){

    var NewState = Object.assign({},state);
    if(action.type === "setProducts"){
        NewState.query = action.data;
    }
    if(action.type === "setProduct"){
        NewState.item = action.data;
    }
    if(action.type == "clear"){
        NewState.query = [];
    }
    return NewState;

} 

const ReducerSearch = {RSearch}

export default ReducerSearch;
