import React, { Component } from 'react';
import {Row, Col, CardImg, InputGroupAddon, InputGroup, InputGroupText, Input, InputGroupButtonDropdown} from 'reactstrap';
import Logo from "../assets/images/Logo_ML.png";
import Find from "../assets/images/ic_Search.png";
import { connect } from 'react-redux';
import DAO from "../components/Dao";

class Header extends Component{

    constructor(props) {
		super(props);
        this.state = {
            find:'',
        };
    }

    componentDidMount(){
        var find = localStorage.getItem("find");
        if(this.state.find == "" && find != null){
            this.setState({find},() => {
                this.callFind();
            });
        }
    }

    callFind(){
        localStorage.setItem("find",this.state.find);
        this.props.callDispatch("findProducts",this.state.find);
    }

	render(){
        const pos = {logo:"1", barSerch:"11"}
		return(
            <Row>
                <Col xs={pos.logo} sm={pos.logo} md={pos.logo} className="logo" >
                    <a href='/'>Donde comprar y vender<CardImg src={Logo}/></a>
                </Col>
                <Col xs={pos.barSerch} sm={pos.barSerch} md={pos.barSerch} className="barSearch">
                    <InputGroup>
                        <Input type="text" id="name" placeholder="Nunca dejes de buscar" defaultValue={this.state.find} onChange={(find) => {this.setState({find:find.target.value})}} onKeyDown={(e) => {if (e.key === 'Enter') this.callFind()}}/>
                        <InputGroupAddon addonType="prepend" onClick={this.callFind.bind(this)}>
                            <InputGroupText><CardImg width="100%" src={Find}/></InputGroupText>
                        </InputGroupAddon>
                    </InputGroup>
                </Col>
            </Row>
		)
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(type, data) => {
            DAO.get(dispatch, type, data);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
