import React, { Component } from 'react';
import {Card, CardBody, CardImg,Row, Col, Container} from 'reactstrap';
import Shipping from "../assets/images/ic_shipping.png";
import CurrencyFormat from 'react-currency-format';
import { connect } from 'react-redux';

class Content extends Component{
	constructor(props) {
		super(props);
		this.state = {}
		this.cardProduct = this.cardProduct.bind(this);
    }

    componentDidMount(){

    }

	cardProduct(obj){
		try{
		var result = obj.query.items;
		var product = [];
		const pos = {img:"2", content:"10", info:"9", from:"2"}
		console.log(result.length);
		result.forEach(val => {
			console.log(val);
			product.push(
				<Row key={val.id} className="separation">
					<Col xs={pos.img} sm={pos.img} md={pos.img} className="h-100 d-table no-gutter">
						<a href='/' className="d-table-cell align-middle prodImg">
							<CardImg src={val.picture}/>
						</a>
					</Col>
					<Col xs={pos.content} sm={pos.content} md={pos.content} className="no-gutter h-100 d-table">
						<Row className="info">
							<Col xs={pos.info} sm={pos.info} md={pos.info}>
								<div className="price">
									<a href="#">
										<CurrencyFormat value={val.price.amount} displayType={'text'} decimalSeparator={"."} fixedDecimalScale={false} thousandSeparator={true} prefix={'$'} renderText={value => value} />
									</a> <div className="shipping"><CardImg width='100%' src={Shipping}/></div></div>
								<div>
									<div className="inf1">{val.title}</div>
									<div className="inf2">{val.condition}</div>
								</div>
							</Col>
							<Col xs={pos.from} sm={pos.from} md={pos.from} className="d-table-cell align-bottom from">{val.state}</Col>
						</Row>
					</Col>
				</Row>
			);
		});

		}catch(e){}
		return product;
	}

	render(){
		return(
			<Card>
				{this.cardProduct(this.props.obj)}
			</Card>
		)	
	}
}

const mapStateToProps = (state) => {
    return{
        obj:state.ReducerSearch,
    }
}

const mapDispatchToProps = (dispatch) => {
	return {
        callDispatch:(type, self) => {
            dispatch({type: type, data: self });
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Content);
