import Enum from "./Enum";

function getProducts(dispatch, textFind){
    var queryUrl = Enum.routes.UrlItems+textFind;
    apiCall(queryUrl, dispatch, "setProducts");
}

function getProduct(dispatch, idFind){
    var queryUrl = Enum.routes.UrlItem+idFind;
    console.log(queryUrl);
    apiCall(queryUrl, dispatch, "setProduct");
}

function apiCall(queryUrl, dispatch, type){
    fetch(queryUrl)
    .then(data => data.json())
    .then(data => { dispatch({ type, data}) })
    .catch(function (error) {console.log("Error cargando datos " + error);}); 
}

const DAO = { getProducts, getProduct }

export default DAO;
